sql.if:
and (



			and cate_code1 = #{searchVO.cate_code1}


			and cate_code2 = #{searchVO.cate_code2}


			and goods_name like CONCAT('%', #{searchVO.goods_name}, '%')


			 and sale_price >= #{searchVO.min} 


			 and sale_price <= #{searchVO.max} 


)

mapper.update:
update goods
set hit = hit + 1
where no = #{no}

mapper.insert:
INSERT INTO goods_sizeColor(goods_no, size_no, color_no) SELECT goods_no, size_no, color_no from (
SELECT #{vo.goods_no} AS goods_no, #{vo.size_no} AS size_no, #{vo.color_no, jdbcType=INTEGER} AS color_no
) t

INSERT INTO goods_option(goods_no, option_name) SELECT goods_no, option_name from (
SELECT #{vo.goods_no} AS goods_no, #{vo.option_name} AS option_name
) t

INSERT INTO price( goods_no, price, sale_price, discount, discount_rate, delivery_charge, saved_rate
,sale_startDate
,sale_endDate
) VALUES ( #{goods_no}, #{price}, #{sale_price, jdbcType=INTEGER}, #{discount, jdbcType=INTEGER}, #{discount_rate, jdbcType=INTEGER}, #{delivery_charge}, #{saved_rate}
,#{sale_startDate}
,#{sale_endDate}
)

mapper.update:
UPDATE goods
SET 
	title = #{title},
	content = #{content},
	writer = #{writer}
WHERE no = #{no} and pw = #{pw}

mapper.delete:
DELETE from goods
WHERE 
	no = #{no}
AND
	pw = #{pw}

mapper.select:
SELECT size_no, cate_code1, cate_code2, size_name
FROM basic_size
WHERE cate_code1 = #{cate_code1}

SELECT color_no, cate_code1, cate_code2, color_name
FROM basic_color
WHERE cate_code1 = #{cate_code1}
