mapper.update:
update goods
set hit = hit + 1
where no = #{no}

mapper.insert:
INSERT INTO category (cate_code1, cate_code2, cate_name)
SELECT
    #{cate_code1},
    IFNULL(MAX(cate_code2), 0) + 1,
    #{cate_name}
FROM (
    SELECT cate_code2
    FROM category
    WHERE cate_code1 = #{cate_code1}
) t

mapper.update:
UPDATE goods
SET 
	title = #{title},
	content = #{content},
	writer = #{writer}
WHERE no = #{no} and pw = #{pw}

mapper.delete:
DELETE from goods
WHERE 
	no = #{no}
AND
	pw = #{pw}

mapper.select:
SELECT size_no, cate_code1, cate_code2, size_name
FROM basic_size
WHERE cate_code1 = #{cate_code1}

SELECT color_no, cate_code1, cate_code2, color_name
FROM basic_color
WHERE cate_code1 = #{cate_code1}
